package Ifelse;
	//Java Program to Check Whether the Character is Vowel or Consonant

public class CharaerIsVowelOrConsonant {

	

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char ch='A';
		if (ch=='a'||ch=='A'||ch=='e'||ch=='i'||ch=='o'||ch=='u') {
			System.out.println("this are Vowel");
		}
		else {
			System.out.println("this are Consonant");
		}
		
	}

}
