package Ifelse;

public class YearIsLeapYearOrNot {


		public static void main(String[] args) {
			// TODO Auto-generated method stub
			int start=2000;
			int first=2001;
			
			
			if (start%4==0) {
				System.out.println("this is leap year ");
				
			}else {
				System.out.println("this is Not leap year ");
			}
			
			if (first%4==0) {
				System.out.println("this is leap year ");
				
			}else {
				System.out.println("this is Not leap year ");
			}
			
			
		}

	}


